import { useRef, useEffect } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import { useGSAP } from "@gsap/react";

gsap.registerPlugin(ScrollTrigger, useGSAP);

const SplitText = ({
  text,
  className = "",
  delay = 100,
  duration = 0.6,
  ease = "power3.out",
  from = { opacity: 0, y: 40 },
  to = { opacity: 1, y: 0 },
  tag = "p",
}) => {
  const ref = useRef();

  useGSAP(() => {
    if (!ref.current) return;

    // Split text manually (free alternative)
    const chars = ref.current.innerText.split("");
    ref.current.innerHTML = chars
      .map((c) => `<span class="char" style="display:inline-block">${c}</span>`)
      .join("");

    gsap.fromTo(
      ref.current.querySelectorAll(".char"),
      { ...from },
      {
        ...to,
        ease,
        duration,
        stagger: delay / 1000,
        scrollTrigger: {
          trigger: ref.current,
          start: "top 90%",
          once: true,
        },
      }
    );
  });

  const Tag = tag;

  return (
    <Tag ref={ref} className={className} style={{ display: "inline-block" }}>
      {text}
    </Tag>
  );
};

export default SplitText;
